import React, { useState } from 'react';
import styled from 'styled-components';

const SearchInput = styled.input`
  height: 32px;
  width: 240px;
  border-radius: 50px;
  transition: all 0.3s;
  padding: ${(props) => (props.status ? '16px' : '16px 40px')};
  background-color: rgb(240, 242, 245);
  border: none;
  outline: none;
  letter-spacing: 1px;
  font-size: 16px;
`;

const FormWithIcon = styled.form`
  position: relative;
  overflow: hidden;
  transition: all 0.3s;
  transform-origin: center right;
  transform: ${(props) => (props.status ? 'scaleX(1.1)' : '')};
  &:after {
    content: '';
    width: 24px;
    height: 24px;
    background: url('data:image/svg+xml;base64,')
      50% 50% no-repeat;
    background-size: 100%;
    position: absolute;
    left: 10px;
    top: 5px;
    transition: all 0.3s;
    transform: ${(props) =>
      props.status ? 'translateX(-10px) scale(0.75)' : ''};
    opacity: ${(props) => (props.status ? '0' : '')};
  }
`;

export default function SearchBox() {
  const [inputState, setInputState] = useState();

  return (
    <FormWithIcon status={inputState}>
      <SearchInput
        type="search"
        status={inputState}
        onFocus={() => setInputState(true)}
        onBlur={() => setInputState(false)}
        placeholder="Search Wishlist"
      />
    </FormWithIcon>
  );
}
